// Get all unique categories
const CATEGORIES = [...new Set(PRODUCTS.map(p => p.category))];

// Get all unique brands
const BRANDS = [...new Set(PRODUCTS.map(p => p.brand))];

// Helper functions
function getProductById(id) {
  return PRODUCTS.find(p => p.id === id);
}

function getProductsByCategory(category) {
  return PRODUCTS.filter(p => p.category === category);
}

function getFeaturedProducts() {
  return PRODUCTS.filter(p => p.featured);
}

function searchProducts(query) {
  const q = query.toLowerCase();
  return PRODUCTS.filter(p => 
    p.name.toLowerCase().includes(q) || 
    p.brand.toLowerCase().includes(q) ||
    p.category.toLowerCase().includes(q)
  );
}

function getDiscountPercentage(product) {
  if (!product.rrp) return 0;
  return Math.round((1 - product.price / product.rrp) * 100);
}

function formatPrice(price) {
  return '£' + price.toFixed(2).replace(/\B(?=(\d{3})+(?!\d))/g, ',');
}

// Export for use in other modules
if (typeof module !== 'undefined' && module.exports) {
  module.exports = { PRODUCTS, CATEGORIES, BRANDS, getProductById, getProductsByCategory, getFeaturedProducts, searchProducts, getDiscountPercentage, formatPrice };
}

